import {useState, useEffect} from 'react';
import {Outlet, Link} from'react-router-dom';

function AdminDashboard({admin})  {
    const [managedType,  setManagedType] = useState('')

    function handleManagedComponent(linkType) {
        if(linkType === managedType) {
            setManagedType('')
        }
        else {
            setManagedType(linkType)
        }
    }

    const managedComponent = () => {
        if(managedType === 'courses') {
            return <Outlet name='courses' />
        }
        else if(managedType === 'teachers') {
            return <Outlet name='teachers' />
        }
        else if(managedType ==='students') {
            return <Outlet name='students' />
        }
        else {
            return null
        }
    }

    return (
        <div>
            <h2>Admin Controls</h2>
            <div>
                <Link onClick={handleManagedComponent('courses')}>Manage Courses</Link>
            </div>
            <br />
            <div>
                <Link onClick={handleManagedComponent('teachers')}>Manage Teachers</Link>
            </div>
            <br />
            <div>
                <Link onClick={handleManagedComponent('students')}>Manage Students</Link>
            </div>
            <br />
            {

            }
        </div>
    )
}

export default AdminDashboard;
