import { useState, useEffect } from "react";
import { useParams, useOutletContext } from "react-router-dom";
import {Formik, useFormik} from 'formik'
import * as yup from 'yup'

function Verify(){
    const params = useParams()
    const [user, setUser] = useState(null);
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [refreshPage, setRefreshPage] = useState(false);
    const [isVerified, setIsVerified] = useState(false);

    useEffect(() => {
        setIsLoading(true)
        fetch(`/users/${params.userId}`)
           .then((response) => response.json())
           .then((data) => {
                setUser(data)
                setIsLoading(false)
                console.log(data)
            })
           .catch((error) => {
                setError(error.message)
                setIsLoading(false)
            })
    }, [])

    const intialToken = {
        email:  '',
        token: ''
    }

    const tokenSchema = yup.object({
        email: yup.string().email('Invalid email format').required('Email is required'),
        token: yup.number().required('Verification code is required')
    })

    const tokenFormik = useFormik({
        initialValues: intialToken,
        validationSchema: tokenSchema,
        onSubmit: (values) => {
            console.log(values)
            if (values.token === user['verification_code']) {
                setIsLoading(true)
                setIsVerified(true)
                console.log('Correct Token')
            }
            else {
                setIsLoading(false)
                console.log('Invalid Verification Code')
            }
        }
    })



    function showVerification(email) {
        return (
            <div>
                <h1>Verify User</h1>
                <form onSubmit={tokenFormik.handleSubmit}>
                    <label htmlFor="email">Email:</label>
                    <br />
                    <input
                        type="text"
                        id="email"
                        disabled={true}
                        onChange={tokenFormik.handleChange}
                        value={email}
                    />
                    <br />
                    <label htmlFor="token">Verification Code:</label>
                    <br />
                    <input
                        type="text"
                        id="token"
                        onChange={tokenFormik.handleChange}
                        value={tokenFormik.values.token}
                    />
                    <p style={{ color: 'red' }}>{error}</p>
                    <br />
                    <button type="submit">Verify</button>
                </form>
            </div>
        )
    }

    function showDetailsForm() {

    }


    if (isLoading) { return <p>Loading...</p> }
    else {
        if (isVerified === true) {

        }
        else {
            if (user) {
                return showVerification(user.email)  // Display the verification form
            }

        }
    }
}

export default Verify;
