import { useState, useEffect } from "react";
import { useParams, useOutletContext } from "react-router-dom";
import {Formik, useFormik} from 'formik'
import * as yup from 'yup'

function Verify(){
    const params = useParams()
    const [user, setUser] = useState(null);
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [refreshPage, setRefreshPage] = useState(false);
    const [isVerified, setIsVerified] = useState(false);
    const [initialDetails, setInitialDetails] = useState({
        firstName: '',
        lastName: '',
        verified: false,
        password: '',
        confirmPassword: '',
    });

    useEffect(() => {
        setIsLoading(true)
        fetch(`/users/${params.userId}`)
           .then((response) => response.json())
           .then((data) => {
                setUser(data)
                setIsLoading(false)
                console.log(data)
            })
           .catch((error) => {
                setError(error.message)
                setIsLoading(false)
            })
    }, [])

    const initialToken = {
        token: ''
    }

    const tokenSchema = yup.object({
        token: yup
            .number()
            .required('Verification code is required')
            .typeError('Enter a 4 digit number')
            .max(9999, 'Enter a 4 digit number')
    })

    const tokenFormik = useFormik({
        initialValues: initialToken,
        validationSchema: tokenSchema,
        onSubmit: (values) => {
            //console.log(values)
            if (values.token === user['verification_code']) {
                //setIsLoading(true)
                setIsVerified(true)
                setInitialDetails({
                    firstName: user['first_name'],
                    lastName: user['last_name'],
                    verified: true,
                    password: '',
                    confirmPassword: '',
                })
                console.log('Correct Token')
            }
            else {
                setIsLoading(false)
                console.log('Invalid Verification Code')
            }
        }
    })


    function showVerification(email) {
        return (
            <div>
                <h1>Verify User</h1>
                <form onSubmit={tokenFormik.handleSubmit}>
                    <label htmlFor="email">Email:</label>
                    <br />
                    <input
                        type="text"
                        id="email"
                        value={email}
                        disabled
                    />
                    <br />
                    <label htmlFor="token">Verification Code:</label>
                    <br />
                    <input
                        type="text"
                        id="token"
                        onChange={tokenFormik.handleChange}
                        value={tokenFormik.values.token}
                    />
                    <p style={{ color: "red" }}> {tokenFormik.errors.token}</p>
                    <br />
                    <button type="submit">Verify</button>
                </form>
            </div>
        )
    }

    const detailsSchema = yup.object({
        firstName: yup.string().required('First Name is required'),
        lastName: yup.string().required('Last Name is required'),
        password: yup.string().required('Password is required')
    })

    const detailsFormik = useFormik({
        initialValues: initialDetails,
        validationSchema: tokenSchema,
        onSubmit: (values) => {
            //console.log(values)
            if (values.token === user['verification_code']) {
                //setIsLoading(true)
                setIsVerified(true)
                console.log('Correct Token')
            }
            else {
                setIsLoading(false)
                console.log('Invalid Verification Code')
            }
        }
    })
    function showDetailsForm(email) {
        <div>
            <h1>Verify User</h1>
            <form onSubmit={detailsFormik.handleSubmit}>
                <label htmlFor="email">Email:</label>
                <br />
                <input
                    type="text"
                    id="email"
                    value={email}
                    disabled
                />
                <br />
                <label htmlFor="firstName">First Name:</label>
                <br />
                <input
                    type="text"
                    id="firstName"
                    onChange={detailsFormik.handleChange}
                    value={detailsFormik.values.firstName}
                />
                <p style={{ color: "red" }}> {detailsFormik.errors.firstName}</p>
                <br />
                <button type="submit">Verify</button>
            </form>
        </div>
    }

    if (isLoading) { return <p>Loading...</p> }
    else {
        if (user){
            if (isVerified === true) {
                return showDetailsForm(user.email)
            }
            else {
                return showVerification(user.email)
            }
        }

    }
}

export default Verify;
