import { useState, useEffect } from "react";
import { useParams, useOutletContext } from "react-router-dom";
import {useFormik} from 'formik'
import * as yup from 'yup'

function Verify(){
    const params = useParams()
    const [user, setUser] = useOutletContext();
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [refreshPage, setRefreshPage] = useState(false);

    useEffect(() => {
        setIsLoading(true)
        fetch(`/users/${params.userId}`)
           .then((response) => response.json())
           .then((data) => {
                setUser(data)
                setIsLoading(false)
                console.log(data)
            })
           .catch((error) => {
                setError(error.message)
                setIsLoading(false)
            })
    }, [])

    const intialToken = {
        email:  user.email,
        token: ''
    }

    const tokenSchema = yup.object({
        email: yup.string().email('Invalid email format').required('Email is required'),
        token: yup.string().required('Verification code is required')
    })

    const tokenFormik = useFormik({
        initialValues: intialToken,
        validationSchema: tokenSchema,
        onSubmit: (values) => {
            setIsLoading(true)
        }
    })

    function handleSubmit(e) {
        e.preventDefault()
        if (e.target.token.value === user['verification_code'] )
        {
            console.log('Correct token')
        }
        else {
            console.log('Invalid token')
        }
    }

    if (isLoading) { return <p>Loading...</p>}
    return (
        <div>
            <h1>Verify User</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="email">Email:</label>
                <br />
                <input type="text" id="email" disabled value={user.email} />
                <br />
                <label htmlFor="token">Verification Code:</label>
                <br />
                <input type="text" id="token" required />
                <p style={{color:'red'}}>{error}</p>
                <br />
                <button type="submit">Verify</button>
            </form>
        </div>
    )
}

export default Verify;
