import { useEffect, useState} from 'react'
import { useParams, Link, useOutletContext } from 'react-router-dom'

function CoursePage() {
    const [user, setUser] = useOutletContext()
    const params = useParams()
    const [course, setCourse] = useState(null)
    const [isLoading, setIsLoading] = useState(true)
    const [assignments, setAssignments] = useState([])

    useEffect(() => {
        fetch(`/courses/${params.courseId}`)
        .then(response => response.json())
        .then(data => {
            //console.log(user)
            setCourse(data)
            //console.log(data)
            if (data.assignments.length > 0) {
                fetch(`/courses/${params.courseId}/assignments`)
                    .then(response => response.json())
                    .then(data => {
                        console.log(data)
                        setIsLoading(false)
                        setAssignments(data)
                    })
            }  
        })


    }, [])

    let mappedAssignments = []

    if (user) {
        if (user.user_type === 'teacher') {
            if (assignments.length === 0) {
                console.log(assignments)
                mappedAssignments = <li key="no-assignments">No assignments found.</li>
            }
            else {
                mappedAssignments = assignments.map((assignment) => {
                    return <li key={assignment.id}>{assignment.title} - Due: {assignment.due_date}</li>
                })
            }
        }
        if (user.user_type === 'student') {

        }
    }
    if (isLoading) {<p>Loading...</p>}
    if (!course) {
        return <p>Course not found.</p>
    }
    else {
        return (
            <div>
                <h1>{course.name}</h1>
                <p>Description: <br />{course.description}</p>
                <h3>Assignments:</h3>
                <ul>
                    {mappedAssignments}
                </ul>
                {user.user_type === 'teacher' ? <></> : <button>Create Assignment</button>}
            </div>
        )
    }
}

export default CoursePage;
